css:
.linter-ui-plus.panel {
  display: flex;
  flex-direction: column;
}
.linter-ui-plus.panel .table-container {
  overflow-y: auto;
  flex: 1;
}
.linter-ui-plus.panel .table-container > table {
  width: 100%;
}
.linter-ui-plus.panel .panel-row-group {
  width: 100%;
}
.linter-ui-plus .panel-row {
  align-items: center;
  cursor: default;
  -webkit-user-select: none;
  width: 100%;
  height: 30px;
}
.linter-ui-plus .panel-row > * {
  vertical-align: middle;
  padding-top: 4px;
  padding-bottom: 4px;
}
.linter-ui-plus .panel-row .marker-focus {
  min-width: 2px;
  max-width: 2px;
  width: 2px;
}
.linter-ui-plus .panel-row:hover {
  background-color: #262931;
}
.linter-ui-plus .panel-row.panel-row-selected {
  background-color: #31363f;
  color: #d7dae0;
}
.linter-ui-plus.panel-focus .panel-row.panel-row-selected .marker-focus {
  background-color: #0088ff;
}
.linter-ui-plus.panel .btn-toolbar {
  padding: 5px 7px;
  display: flex;
  align-items: center;
  background: #21252b;
  border-top: 1px solid #181a1f;
  border-bottom: 1px solid #181a1f;
}
.linter-ui-plus.panel .btn-toolbar > * {
  float: none;
}
.linter-ui-plus.panel .btn-toolbar > .btn,
.linter-ui-plus.panel .btn-toolbar .btn-group {
  margin-left: 4px;
  margin-right: 4px;
}
.linter-ui-plus.panel .btn-toolbar .btn-group {
  display: flex;
  align-items: center;
  flex-shrink: 0;
}
.linter-ui-plus.panel .btn-toolbar .spacer {
  flex: 1;
}
.linter-ui-plus.panel .btn-toolbar atom-text-editor {
  flex: 1.5;
  flex-shrink: 1;
}
